const inputs = $input.all();

let slack_user_id;
let canvas_user_id;
let student_name;
let email;

for (const item of inputs) {
  const data = item.json;

  // slack info
  if (typeof data?.user?.id === 'string') {
    slack_user_id = data.user.id;
  }

  // canvas info
  if (typeof data.id === 'number' && data.root_account) {
    canvas_user_id = data.id;
    student_name = data.name;
    email = data.email || data.login_id;
  }
}

if (!slack_user_id || !canvas_user_id || !student_name) {
  throw new Error("Missing required data from Slack or Canvas input.");
}

// profile
const profile = {
  student_name,
  slack_user_id,
  canvas_user_id,
  email,
};

return {
  json: profile
};

// get data from Slack trigger event
