// unpack incoming JSON
const {
  student_name,
  canvas_user_id,
  syncs = [],
} = items[0].json;

// keyword maps
const keywordMap = {
  cad: {
    1: ["design","create","application","development","planning","architecture"],
    2: ["ui","interface","user","form","widget","portal","mobile","policy"],
    3: ["security","access","roles","permissions","acl","authentication"],
    4: ["automation","workflow","business rules","script","flow","logic"],
    5: ["data","external","integration","import","export","web service"],
    6: ["management","deployment","update set","version","maintenance"]
  },
  csa: {
    1: ["platform","navigation","overview","basics","fundamentals"],
    2: ["configuration","instance","setup","customization"],
    3: ["collaboration","knowledge","service catalog","portal","catalog"],
    4: ["self service","automation","workflow","request","business rules","flow","logic"],
    5: ["database","security","tables","fields","users","roles","schema","data","inputs","errors"],
    6: ["migration","integration","import","transform"]
  }
};

// normalize helper
function normalize(text) {
  return text
    .toLowerCase()
    .replace(/[^\w\s]/g, "")
    .replace(/\s+/g, " ")
    .trim();
}

// 1) decide CAD vs CSA
let scores = { cad: 0, csa: 0 };
for (const { assignment } of syncs) {
  const t = normalize(assignment);
  for (const type of ["cad", "csa"]) {
    for (const kw of Object.values(keywordMap[type]).flat()) {
      if (t.includes(kw)) scores[type]++;
    }
  }
}
const examType = scores.csa >= scores.cad ? "csa" : "cad";

// 2) ruleâ€based scoring
const threshold = 2;
const confident = {};
const unknowns = [];

for (const { assignment } of syncs) {
  const t = normalize(assignment);
  let best = { domain: 1, score: 0 };
  for (const [domain, kws] of Object.entries(keywordMap[examType])) {
    const sc = kws.reduce((sum, kw) => sum + (t.includes(kw) ? 1 : 0), 0);
    if (sc > best.score) best = { domain: Number(domain), score: sc };
  }
  if (best.score >= threshold) {
    confident[assignment] = best.domain;
  } else {
    unknowns.push(assignment);
  }
}

// 3) build final mappings (unknowns default to 1)
const finalMappings = {};
for (const { assignment } of syncs) {
  finalMappings[assignment] =
    confident[assignment] !== undefined
      ? confident[assignment]
      : 1;  // default if rule didn't catch it
}

// 4) prepare SQL records, now including percentage/late/missing
const records = syncs.map(({ assignment, percentage, late, missing }) => ({
  exam_type: examType,
  domain: finalMappings[assignment],
  assignment,
  percentage,
  late,
  missing
}));

// 5) output only the requested fields
items[0].json = {
  student_name,
  canvas_user_id,
  records
};

return items;

