const allSessions = $input.all().map(i => i.json);

// filter placeholder entries
const validSessions = allSessions.filter(s => s.session_timestamp);

// de-duplicate by session_timestamp
const seen = new Set();
const uniqueSessions = [];

for (const session of validSessions) {
  const key = session.session_timestamp;
  if (!seen.has(key)) {
    seen.add(key);
    uniqueSessions.push(session);
  }
}

// Separate current + historical data
const currentData = uniqueSessions[0] || {};
const historicalData = uniqueSessions.slice(1);

// Add strengths and weaknesses from the second input (merged input usually)
const mergedInput = allSessions.find(s => s.strengths && s.weaknesses) || {};
currentData.strengths = currentData.strengths || mergedInput.strengths || [];
currentData.weaknesses = currentData.weaknesses || mergedInput.weaknesses || [];

return [{
  json: {
    student_name: mergedInput.student_name || null,
    current_performance: currentData,
    historical_context: {
      previous_sessions: historicalData.length,
      last_interaction: historicalData[0]?.session_timestamp || 'First time',
      grade_progression: historicalData.map(h => ({
        date: h.session_timestamp,
        strengths: h.topic_strengths || [],
        weaknesses: h.topic_weaknesses || []
      })),
      persistent_weaknesses: findPersistentWeaknesses(historicalData),
      improvement_areas: findImprovedAreas(historicalData)
    }
  }
}];

function findPersistentWeaknesses(history) {
  const weaknessCount = {};
  history.forEach(session => {
    session.topic_weaknesses?.forEach(weakness => {
      weaknessCount[weakness] = (weaknessCount[weakness] || 0) + 1;
    });
  });
  return Object.entries(weaknessCount)
    .filter(([_, count]) => count >= 2)
    .map(([topic, count]) => ({ topic, frequency: count }));
}

function findImprovedAreas(history) {
  if (history.length < 2) return [];
  const [recent, older] = history;
  const recentWeak = new Set(recent.topic_weaknesses || []);
  const olderWeak = new Set(older.topic_weaknesses || []);
  return [...olderWeak].filter(w => !recentWeak.has(w));
}

