return rows;

const canvas_user_id = $node["Student Profile"].json.canvas_user_id;
const student_name = $node["Student Profile"].json.student_name;

const grouped = {};

for (const item of items) {
  const data = item.json;

  // make sure it's the correct submission
  if (data.name != String(canvas_user_id)) continue;
  if (!data.assignment.toLowerCase().includes("knowledge sync")) continue;

  // skip if grade or points are invalid
  const grade = parseFloat(data.entered_grade);
  const points = parseFloat(data.points_possible);

  if (isNaN(grade) || isNaN(points) || points <= 0) continue;

  const percentage = (grade / points) * 100;

  const submission = {
    assignment: data.assignment,
    percentage: Math.round(percentage),
    late: data.late,
    missing: data.missing
  };

  // group by student_name
  if (!grouped[student_name]) {
    grouped[student_name] = [];
  }

  grouped[student_name].push(submission);
}

// Final output structure
const result = [];

for (const name in grouped) {
  result.push({
    json: {
      student_name: name,
      canvas_user_id,
      syncs: grouped[name]
    }
  });
}

return result;

